import React, { useState } from 'react';
import DataFetcher from './DataFetcher';
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
  Link
} from 'react-router-dom';
import './App.css';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" component={BookList} />
      </Routes>
    </Router>
  );
}

function BookList() {
  const query = useQuery();
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [total, setTotal] = useState(0);
  const [skip, setSkip] = useState(Number(query.get('skip')) || 0);
  const limit = 6;

  useEffect(() => {
    setSkip(Number(query.get('skip')) || 0);
  }, [query]);

  const handleDataFetched = (fetchedData) => {
    if (fetchedData.error) {
      setError(fetchedData.error);
    } else {
      const booksList = fetchedData.data.map(bookItem => ({
        id: bookItem.id,
        title: bookItem.title,
        cover_url: bookItem.cover_url,
      }));
      setBooks(booksList);
      setTotal(fetchedData.pagination.total);
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <DataFetcher skip={skip} limit={limit} onDataFetched={handleDataFetched} />
        {loading && <p>Loading..</p>}
        {error && <p>Error: {error}</p>}
        {!loading && !error && (
          <div className="main">
            <div className="books">
              {books.map((bookItem) => (
                <div key={bookItem.id} className="book">
                  <img src={bookItem.cover_url} alt={bookItem.title} className="book-cover" />
                  <div className="book-details">
                    <h3 className="book-title">{bookItem.title}</h3>
                  </div>
                </div>
              ))}
            </div>
            <div className="paginator">
              <Link to={`/?skip=${Math.max(skip - limit, 0)}&limit=${limit}`} className={skip === 0 ? 'disabled' : ''}>
                Prev
              </Link>
              <span>Page {Math.ceil(skip / limit) + 1}</span>
              <Link to={`/?skip=${skip + limit}&limit=${limit}`} className={skip + limit >= total ? 'disabled' : ''}>
                Next
              </Link>
            </div>
          </div>

        )}
      </header>
    </div>
  );
}

export default App;
